INSERT INTO posts VALUES (1, 'Most of the world`s biggest problems are because of somebody trying to find a simple answer.', '2022-01-01 00:00:00', 'My thoughts', 1);
INSERT INTO posts VALUES (2, 'Feed a man a fish, feed him for a night.Teach a man to fish, feed him for a lifetime.Teach a man to reason from first principles, he can teach himself to fish.', '2022-01-01 00:00:00', 'My first post', 2);
INSERT INTO posts VALUES (3, 'Perfectionism is not a quest for the best. It is a pursuit of the worst in ourselves, the part that tells us that nothing we do will ever be good enough-that we should try again.', '2022-01-01 00:00:00', 'Perfectionism', 3);
INSERT INTO posts VALUES (4, 'When circumstances aren`t ideal, don`t assume you have no control. You always do. So instead of feeling bad that you can`t do everything, do something!', '2022-01-01 00:00:00', 'Control', 4);
INSERT INTO posts VALUES (5, 'If you do not change direction, you might end up where you are heading.', '2022-01-01 00:00:00', 'My great post', 5);
